import pandas as pdimport jsonwith open('baby.json') as data_file:    data = json.load(data_file)with open('baby2.json') as data_file:    data2 = json.load(data_file)with open('baby3.json') as data_file:    data3 = json.load(data_file)df_accounts = pd.json_normalize(data, record_path=['AccountAllocations', 'Accounts'                                                    ])[['Key', 'Description']]df_accounts2 = pd.json_normalize(data2, record_path=['AccountAllocations', 'Accounts'                                                    ])[['Key', 'Description']]df_accounts3 = pd.json_normalize(data3, record_path=['AccountAllocations', 'Accounts'                                                    ])[['Key', 'Description']]print("Account Names Key:")                              print(df_accounts)df_allocations= pd.json_normalize(data, record_path=['FundAllocationFuture'])[['Manager','Allocation']]df_allocations2= pd.json_normalize(data2, record_path=['FundAllocationFuture'])[['Manager','Allocation']]df_allocations3= pd.json_normalize(data3, record_path=['FundAllocationFuture'])[['Manager','Allocation']]merged_allocations=df_allocations.merge(df_allocations2, on='Manager')merged_allocations=merged_allocations.merge(df_allocations3, on='Manager')print("")print("Account Allocations Table for Applicant 1:")print(df_allocations)print("")print("Account Allocations Table for Applicant 2:")print(df_allocations2)print("")print("Account Allocations Table for Applicant 3:")print(df_allocations3)print("")print("Merged Account Allocations Table for the 3 Applicants:")print(merged_allocations)